ScriptAction.script:
importPackage(net.ipsoft.ipcenter.framework.velocity);
function setVar (name, value) {
    execution.getExecutionVariableForScope(name, executionPointer.getCurrentScope()).setValue(value);
}

var element=eval('('+jsonBody+')');
var elementId=0;
var folderId=0;
var elementTypeId=0;
var elementTypeAttributeId=0;
var k;

for(var i=0;i<element.data.length; i++)
{
    if(element.data[i].attributes)
    {
        for(var j=0; j < element.data[i].attributes.length; j++)
        {
            if(element.data[i].attributes[j].name=='Username'
            && element.data[i].attributes[j].value==lsUsername
            && element.data[i].folder.name==lsFolderName
            )
            {
                elementId=element.data[i].id;
                elementName=element.data[i].name;
                elementTypeId=element.data[i].elementTypeId;
                folderId=element.data[i].folder.id;
                k=i;
                break;
            }            
        }
    }
}
if(elementId > 0)
{
    for(var j=0; j < element.data[k].attributes.length; j++)
    {
        if(element.data[k].attributes[j].name=='Password')
        {
            elementTypeAttributeId=element.data[k].attributes[j].elementTypeAttributeId;
        }
    }
   setVar('elementId',elementId);
   setVar('folderId',folderId);
   setVar('elementTypeId',elementTypeId);
   setVar('elementTypeAttributeId',elementTypeAttributeId);
   setVar('elementName',elementName);
}

