ScriptAction.script:
function sleep(milliseconds) {
  var start = new Date().getTime();
  for (var i = 0; i < 40e7; i++) {
    if ((new Date().getTime() - start) > milliseconds){
      break;
    }
  }
}

var out = "Wait Start "+new Date()+"\n";
sleep(300000);
out += "Wait End "+new Date()+"\n";
out;

